        {
          "@type": "Input",
          "@id": "#ndvi-red-input",
          "name": "red"
        }
      ],
      "hasOutput": [
        {
          "@type": "Output",
          "@id": "#ndvi-result",
          "name": "ndvi"
        }
      ]
