    {
      "@type": "DataLink",
      "@id": "#link-nir",
      "hasSource": {
        "@id": "#nir-band"
      },
      "hasSink": {
        "@id": "#ndvi-nir-input"
      }
    },
    {
      "@type": "DataLink",
      "@id": "#link-red",
      "hasSource": {
        "@id": "#red-band"
      },
      "hasSink": {
        "@id": "#ndvi-red-input"
      }
    }
  ]
}

